'use strict'

var expect = require('chai').expect;
var samlp = require('../lib')
var encoder = require('../lib/encoders');
var fs = require('fs')
var path = require('path')
var zlib = require('zlib');

var requestWithAuthnContextClassRef = '<?xml version="1.0" encoding="UTF-8"?>\n<samlp:AuthnRequest xmlns:samlp="urn:oasis:names:tc:SAML:2.0:protocol" ID="pfxe6c08b2b-e9a1-5d44-9016-f4c2b59add88" AssertionConsumerServiceURL="https://acs" Destination="https://destination" IssueInstant="2013-04-28T22:43:42.386Z" ProtocolBinding="urn:oasis:names:tc:SAML:2.0:bindings:HTTP-POST" Version="2.0"><saml:Issuer xmlns:saml="urn:oasis:names:tc:SAML:2.0:assertion">http://sp</saml:Issuer><ds:Signature xmlns:ds="http://www.w3.org/2000/09/xmldsig#">\n  <ds:SignedInfo><ds:CanonicalizationMethod Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"/>\n    <ds:SignatureMethod Algorithm="http://www.w3.org/2000/09/xmldsig#rsa-sha1"/>\n  <ds:Reference URI="#pfxe6c08b2b-e9a1-5d44-9016-f4c2b59add88"><ds:Transforms><ds:Transform Algorithm="http://www.w3.org/2000/09/xmldsig#enveloped-signature"/><ds:Transform Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"/></ds:Transforms><ds:DigestMethod Algorithm="http://www.w3.org/2000/09/xmldsig#sha1"/><ds:DigestValue>1GJyc/S+0PTuqU1hp6grJy3u4Dk=</ds:DigestValue></ds:Reference></ds:SignedInfo><ds:SignatureValue></ds:SignatureValue>\n<ds:KeyInfo><ds:X509Data><ds:X509Certificate></ds:X509Certificate></ds:X509Data></ds:KeyInfo></ds:Signature><samlp:NameIDPolicy Format="urn:oasis:names:tc:SAML:1.1:nameid-format:emailAddress" AllowCreate="true"/><samlp:RequestedAuthnContext Comparison="exact"><samlp:AuthnContextClassRef>http://schemas.openid.net/pape/policies/2007/06/multi-factor</samlp:AuthnContextClassRef></samlp:RequestedAuthnContext></samlp:AuthnRequest>';

var requestWithoutAuthnContextClassRef = '<?xml version="1.0" encoding="UTF-8"?>\n<samlp:AuthnRequest xmlns:samlp="urn:oasis:names:tc:SAML:2.0:protocol" ID="pfx20d87357-f8ae-db44-a7a9-39c0446a2ee2" AssertionConsumerServiceURL="https://acs" Destination="https://destination" IssueInstant="2013-04-28T22:43:42.386Z" ProtocolBinding="urn:oasis:names:tc:SAML:2.0:bindings:HTTP-POST" Version="2.0"><saml:Issuer xmlns:saml="urn:oasis:names:tc:SAML:2.0:assertion">http://sp</saml:Issuer><ds:Signature xmlns:ds="http://www.w3.org/2000/09/xmldsig#">\n  <ds:SignedInfo><ds:CanonicalizationMethod Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"/>\n    <ds:SignatureMethod Algorithm="http://www.w3.org/2000/09/xmldsig#rsa-sha1"/>\n  <ds:Reference URI="#pfx20d87357-f8ae-db44-a7a9-39c0446a2ee2"><ds:Transforms><ds:Transform Algorithm="http://www.w3.org/2000/09/xmldsig#enveloped-signature"/><ds:Transform Algorithm="http://www.w3.org/2001/10/xml-exc-c14n#"/></ds:Transforms><ds:DigestMethod Algorithm="http://www.w3.org/2000/09/xmldsig#sha1"/><ds:DigestValue>HB+gsJjEBYtMgMwznLms7tXAmmo=</ds:DigestValue></ds:Reference></ds:SignedInfo><ds:SignatureValue></ds:SignatureValue>\n<ds:KeyInfo><ds:X509Data><ds:X509Certificate></ds:X509Certificate></ds:X509Data></ds:KeyInfo></ds:Signature><samlp:NameIDPolicy Format="urn:oasis:names:tc:SAML:1.1:nameid-format:emailAddress" AllowCreate="true"/></samlp:AuthnRequest>';

describe('samlp parse response', function() {
  var cert;

  before(function () {
    cert = fs.readFileSync(path.join(__dirname, 'fixture/samlp.test-cert.pem'));
  });

  describe('SAMLRequest on querystring', function () {
    let parseResult;

    describe('when request has authnContextClassRef', function() {
      it('should return a valid response', function(done){
        samlp.parseRequest({
          query: {
            SAMLRequest: new Buffer(requestWithAuthnContextClassRef).toString('base64'),
            RelayState: '123'
          }
        }, {
          signingCert: cert,
          thumbprints: [ encoder.thumbprint(cert) ],
          relayState: '123'
        }, function(err, result) {
          expect(err).not.to.exist;
          expect(result).to.eql({
            issuer: 'http://sp',
            assertionConsumerServiceURL: 'https://acs',
            destination: 'https://destination',
            id: 'pfxe6c08b2b-e9a1-5d44-9016-f4c2b59add88',
            requestedAuthnContext: {
              authnContextClassRef: 'http://schemas.openid.net/pape/policies/2007/06/multi-factor'
            }
          });

          done();
        })
      });
    });

    describe('when request does not have authnContextClassRef', function() {
      it('should return a valid response', function(done){
        samlp.parseRequest({
            query: {
              SAMLRequest: new Buffer(requestWithoutAuthnContextClassRef).toString('base64'),
              RelayState: '123'
            }
          }, {
            signingCert: cert,
            thumbprints: [ encoder.thumbprint(cert) ],
            relayState: '123'
          }, function(err, result) {
            if (err) {
              done(err);
              return;
            }

            expect(err).not.to.exist;
            expect(result).to.eql({
              issuer: 'http://sp',
              assertionConsumerServiceURL: 'https://acs',
              destination: 'https://destination',
              id: 'pfx20d87357-f8ae-db44-a7a9-39c0446a2ee2'
            });

            done();
          });
      });
    });

    describe('when request is not a valid XML', function(){
      // There was a bug in xmldom causing an infinite loop in this case
      it('should return an empty object', function(done){
        const req = '<samlp:AuthnRequest';
        samlp.parseRequest({
          query: {
            SAMLRequest: new Buffer(req).toString('base64'),
            RelayState: '123'
          }
        }, {
          relayState: '123'
        }, function(err, result) {
          expect(err).to.exist;
          expect(err.message).to.equal('expected null to exist');
          expect(result).to.be.undefined;
          done();
        });
      });
    });

    describe('when request is not a malformed XML', function(){
      it('should return an error', function(done){
        const samlRequestPlain = '<samlp:AuthnRequest foo="bar"></test>';
        encodeAndDeflate(samlRequestPlain, function(err, req) {
          if (err) { return done(err); };

          samlp.parseRequest({
            query: {
              SAMLRequest: req.toString('base64'),
              RelayState: '123'
            }
          }, {
            relayState: '123'
          }, function(err, result) {
            expect(err).to.exist;
            expect(result).to.be.undefined;
            expect(err.message).to.equal('end tag name: test is not match the current start tagName:undefined');
            done();
          });

        });
      });
    });
  });
});


 function encodeAndDeflate(xml, cb){
  zlib.deflateRaw(new Buffer(xml), function (err, buffer) {
    if (err) {return cb(err); }
    cb(null, buffer.toString('base64'));  
  });
};